     */
    public function testConstruction()
    {
        $this->assertEquals('post', $this->object->getAttribute('method'));

        $submit = $this->object->get(PaymentMethods::ELEMENT_SUBMIT);
        $this->assertInstanceOf('Zend\Form\Element\Submit', $submit);
    }

